mapper.select:
select type_id "typeId", p_type_id "pTypeId", type_name "typeName", info_type "infoType", type_route "typeRoute", is_used "isUsed", sort_no "sortNo" from CODE_GE_MULTILEVEL where p_type_id=#{pTypeId} and info_type=#{infoType}
and is_used = #{isUsed}
order by sort_no

mapper.delete:
delete
from CODE_GE_MULTILEVEL
where type_route like '${typeId}%' and info_type=#{infoType}
