mapper.select:
,c.dict_desc "infoTypeName" from info_ge_resource a left join code_ge_restype b on a.type_id=b.type_id left join info_ge_dict c on a.info_type = c.dict_code and c.type_code = 'INFO_TYPE'
a.info_type = #{params.bo.infoType}
and a.type_id = #{params.bo.typeId}
and b.type_route like '${params.bo.typeRoute}%'
and a.res_desc like '%${params.bo.keyWord}%'
group by a.res_id order by a.create_time desc

mapper.update:
update info_ge_resource
set
res_desc = #{resDesc},
is_pic = #{isPic}
where res_id = #{resId}

update info_ge_resource
set ref_nums = ifnull(ref_nums,0)+1
where res_id = #{resId}

update info_ge_resource
set ref_nums = ref_nums-1
where res_id = #{resId}

mapper.insert:
insert into info_ge_resref (
res_id,
info_id,
info_type
) values (
#{resId},
#{infoId},
#{infoType}
)

mapper.delete:
delete from info_ge_resref
where
res_id = #{resId}
and info_id = #{infoId}
and info_type = #{infoType}
