mapper.select:
from info_pad_dform_config a left join INFO_DATA_ACCESS b on a.access_code=b.access_code left join SYS_MENU c on a.menu_id=c.menu_id
a.config_id like '%${params.bo.keyWord}%' or a.form_name like '%${params.bo.keyWord}%'
order by ${params.bo.sort}
${params.bo.order}
order by a.config_id

select count(*)
from info_pad_dform_config
where form_name = #{formName}
